content:
body {
    display: flex;
    --nav-bar-width: 50px;
    --char-panel-with: 0px;
    --settings-panel-width: 0px;
    --drawer-content-panel-width: 0px;
    --authors-note-panel-width: 0px;
    --expression-image-width: 0px;
    --cfgConfig-panel-width: 0px;
    --logprobsViewer-panel-width: 0px;
    --stcdxcodex: 0px;
    --avatar-base-width: 50px;
    --avatar-base-height: 50px;
	--drawer-content-panel-width: calc(100vw - var(--nav-bar-width) - var(--settings-panel-width) - var(--char-panel-width) - var(--authors-note-panel-width) - var(--stcdxcodex));
}

#form_character_search_form {
	gap: 0.2rem;
}

.disabledWIEntry {
    opacity: 1;
    filter: none;
}

#extensionTopBar {
	border-radius: unset !important;
}
#extensionTopBarSearchInput:placeholder-shown {
	border-color: var(--SmartThemeBorderColor);
}

.options-content {
    border-radius: 0px !important;
}

#options {
    border-radius: 0px;
}

body.charListGrid #rm_print_characters_block .character_select .character_select_container {
    max-width: unset !important;
    overflow: hidden;
}

body.bubblechat .mes {
    padding: 6px;
    border-radius: 0px;
}

.wi-card-entry {
    border-color: var(--SmartThemeBorderColor);
    border-radius: 0px;
    padding: 0px;
}

.inline-drawer-header {
    background: var(--black50a);
    padding: 0px 6px 0px 6px;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
    border-bottom-left-radius: 0px !important;
}

.inline-drawer-content {
    padding: 5px;
    background: var(--black50a);
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 5px !important;
    border-bottom-left-radius: 0px !important;
}

.standoutHeader {
    border-top-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
}

#extensions_settings .inline-drawer-toggle.inline-drawer-header, #extensions_settings2 .inline-drawer-toggle.inline-drawer-header, #user-settings-block h4, .standoutHeader {
    background: var(--black50a);
    margin: 6px 0px 0px 0px;
    border-top-left-radius: 5px !important;
    border-top-right-radius: 0px !important;
    border-bottom-right-radius: 5px !important;
    border-bottom-left-radius: 0px !important;

    border: 1px solid var(--SmartThemeBorderColor);
    transition: none;
    margin-bottom: 0px;
}

.userSettingsInnerExpandable {
    border: 1px solid;
    border-color: var(--SmartThemeBorderColor);
    padding: 2px 5px !important;
    margin: 5px 0 0 0 ;
}

.stcdx--root {
    border-radius: unset;
	filter: none;
	transition: none;
}

.stcdx--codex > .stcdx--header > .stcdx--action {
  opacity: 1;
  transition: none;
}
.stcdx--codex > .stcdx--header > .stcdx--action.stcdx--menu > .stcdx--books {
background-color: var(--SmartThemeBlurTintColor) ;
}

body:has(#stcdx--codex.stcdx--active) #WorldInfo {  
    max-width: var(--drawer-content-panel-width);
}

body.charListGrid:has(#right-nav-panel.openDrawer):not(#rm_ch_create_block) {
    --char-panel-width: 700px;
}

body:has(#right-nav-panel.openDrawer) {
    --char-panel-width: 360px;
}

body:has(#rm_ch_create_block:is([style*="display: block; opacity: 1;"])) {
    --char-panel-width: 360px !important;
}

body:has(.stcdx--root.stcdx--codex.stcdx--active) {
    --stcdxcodex: 400px !important;
}

body:has(#right-nav-panel.openDrawer) {
    --char-panel-width: 360px ;
}

body:has(#right-nav-panel.closedDrawer) {
    --char-panel-width: 0px !important;
}

body:has(#left-nav-panel.openDrawer) {
    --settings-panel-width: 360px !important;
}

body:has(#expression-image:not([src=""])) {
    --expression-image-width: 25vw;
}

body:has(#expression-image:not([src=""])).waifuMode {
    --expression-image-width: 0px;
}

body:has(.zoomed_avatar.draggable) {
    --expression-image-width: 25vw;
}
body:has(.zoomed_avatar.draggable).waifuMode {
    --expression-image-width: 0vw;
}

body:not(:has(.zoomed_avatar.draggable)) {
  --expression-image-width: 0px;
}

body:has(#floatingPrompt:is([style*="display: flex; opacity: 1;"])) {
    --authors-note-panel-width: 400px;
}

body:has(#floatingPrompt.maximized) {
    --authors-note-panel-width: 0px;
}

body:has(#cfgConfig:is([style*="display: flex; opacity: 1;"])) {
    --cfgConfig-panel-width: 360px;
}

body:has(#cfgConfig.maximized) {
    --cfgConfig-panel-width: 0px;
}

body:has(#logprobsViewer:is([style*="display: flex; opacity: 1;"])) {
    --expression-image-width: 360px;
}

body:has(#logprobsViewer.maximized) {
    --expression-image-width: 0px;
}

#top-bar {
    border-bottom: unset;
    background-color: var(--SmartThemeBlurTintColor);
    margin: unset;
    width: unset;
    height: unset;
    position: unset;
}

#sheld {
    left: calc(var(--nav-bar-width) + var(--char-panel-width) + var(--cfgConfig-panel-width)) !important;
    justify-content: left;
    margin: unset;
    height: 100vh;
    height: 100dvh;
    max-height: unset;
    top: 0;
    width: calc( 100vw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--char-panel-width) - var(--settings-panel-width) - var(--stcdxcodex));
    width: calc( 100dvw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--char-panel-width) - var(--settings-panel-width) - var(--stcdxcodex));
    background: rgba(var(--primaryBgCol), var(--primaryBgAlpha));
    backdrop-filter: blur(var(--SmartThemeBlurStrength));
    -webkit-backdrop-filter: blur(var(--SmartThemeBlurStrength));
}

#chat,
#form_sheld {
    width: calc( 100vw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--char-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--settings-panel-width) - var(--stcdxcodex));
    margin: 0px auto 0 auto;
}

#cfgConfig {
    left: calc(var(--nav-bar-width) + var(--char-panel-width));
    width: var(--cfgConfig-panel-width);
    height: 100vh;
    height: 100dvh;
    z-index: 40 !important;
    max-height: unset;
    border-radius: unset;
    border: unset;
    min-width: unset;
}

#CharListButtonAndHotSwaps {
    padding: 6px;
}

body.charListGrid #rm_print_characters_block {
    padding-right: 10px;
}

#right-nav-panel {
    left: var(--nav-bar-width);
    width: var(--char-panel-width);
    height: 100vh;
    height: 100dvh;
    z-index: 10 !important;
    max-height: unset;
    border-radius: unset;
    border: unset;
    min-width: unset;
    padding: 0px;
}

#top-settings-holder {
    background-color: var(--SmartThemeBlurTintColor);
    margin: unset;
    position: unset;
    height: 100vh;
    height: 100dvh;
    flex-direction: column;
    padding: 16px 8px;
    gap: 16px;
    justify-content: unset;
    width: var(--nav-bar-width);

    &::before {
        content: "";
        width: 100%;
        aspect-ratio: 1/1;
        background-image: url("img/five.png");
        background-size: cover;
        background-position: center center;
        border-radius: 50%;
    }
}


.user-settings-v2-block.drawer-content.stcdx--active {
	max-width: var(--drawer-content-panel-width);
}

body .stcdx--tooltip {
  position: absolute;
  bottom: 0;
  right: 0;
  top: calc(2em);
  max-height: unset;
  max-width: unset;
  height: unset;
  width: calc((100vw - var(--sheldWidth)) / 2) !important;
}

.drawer-content {
    width: var(--drawer-content-panel-width);
    border-radius: unset;
    left: calc(var(--nav-bar-width) + var(--char-panel-width));
    margin: 0;
    z-index: 20 !important;
    box-shadow: none;
    top: 0;
    max-height: calc(100vh - var(--bottomFormBlockSize));
    padding: 6px;
}

#expression-holder {
    right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));
    left: unset;
    justify-content: right;
}

body.waifuMode .zoomed_avatar {
    padding: 0 !important;
    border: 0 !important;
    background-color: transparent !important;
    right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));
    left: unset;
    z-index: 20 !important;
}

.zoomed_avatar {
    Width: var(--expression-image-width);
    padding: 0 !important;
    border: 0 !important;
    background-color: transparent !important;
    left: unset !important;
    right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));
    z-index: 30 !important;
}

body .stcdx--root {
    right: calc(var(--settings-panel-width) + var(--authors-note-panel-width));
    background-color: var(--SmartThemeBlurTintColor) !important;;
    width: var(--stcdxcodex);
    z-index: 50 !important;
    border-radius: unset;
	filter: unset;

}

body .stcdx--codex > .stcdx--header > .stcdx--action {
	transition: unset !important;
	opacity: 1;

}

body .stcdx--root > .stcdx--content {
	transition: unset !important;
}

.stcdx--codex {
	transition: unset !important;
}

.draggable {
    border-radius: 0px;
}

#logprobsViewer:not(.maximized) {
    right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex));  
    z-index: 30 !important;
    left: unset;
    width: var(--expression-image-width);
}

#logprobsViewer.maximized {
     z-index: 50 !important;
}

#expression-wrapper {
    z-index: 20 !important;
    width: 0 !important;
    height: 0 !important;
}

#floatingPrompt:not(.maximized) {
    z-index: 40 !important;
    right: var(--settings-panel-width);
    left: unset;
    width: var(--authors-note-panel-width);
    border-radius: 0;
    height: 100vh;
    height: 100dvh;
    max-height: unset;
    box-shadow: none;
}

#qr--popout {
     z-index: 9999 !important;
}

#chat {
    padding: 0px;
    max-height: unset;
    backdrop-filter: none;
    border: 1px solid var(--SmartThemeBorderColor) !important;
}

#floatingPrompt.maximized {
    z-index: 50 !important;
}

#left-nav-panel {
    right: 0;
    left: unset;
    width: var(--settings-panel-width);
    border: unset;
    height: 100vh;
    height: 100dvh;
    max-height: unset;
}

#vv--root.vv--maximized {
    z-index: 50 !important;
    width: var(--drawer-content-panel-width);
    left: calc(var(--nav-bar-width) + var(--char-panel-width));
    min-height: 100px !important;
    max-height: calc(100vh - var(--bottomFormBlockSize));
    bottom: 0;
}

#vv--root {
    z-index: 50 !important;
    width: var(--drawer-content-panel-width);
    left: calc(var(--nav-bar-width) + var(--char-panel-width));
    max-width: 100vw !important;
    min-height: 100px !important;
    max-height: calc(100vh - var(--bottomFormBlockSize));
    bottom: unset;
}

#send_form {
    border-radius: 0px;
}

#qr--settings #qr--set-qrList .qr--set-qrListContents > .qr--set-item > :nth-child(5) {
    flex: inherit;
}

#floatingPrompt, #cfgConfig{
	padding: 0px;
	padding-top: 30px;
}

.checkbox_label {
	overflow: hidden;
}

.scrollY{
	padding-right: 6px;
}

.csss--input::selection, .csss--content::selection {
    color: #ffffff !important;
    background: #ff0000 !important;
}

.bg_list{
	width: unset;
}

.avatar.inline_avatar.flex.alignitemscenter.textAlignCenter.selectable.character_select {
    --avatar-base-height: 50px !important;
    --avatar-base-width: 50px !important;
    --big-avatar-height-factor: 1 !important;
    --big-avatar-width-factor: 0.8 !important;
    --big-avatar-border-factor: 5 !important;
}

#rm_print_characters_block .ch_name, .avatar-container .ch_name {
    flex: 1 1 auto;
    white-space: nowrap;
    text-wrap-mode: nowrap;
    overflow: hidden;
    text-wrap: wrap;
    text-overflow: ellipsis;
    display: block;
}

body.charListGrid #rm_print_characters_block .bogus_folder_select, body.charListGrid #rm_print_characters_block .character_select, body.charListGrid #rm_print_characters_block .group_select, #user_avatar_block.gridView .avatar-container {
    height: auto;
    max-width: max-content;
    margin: 0px;
    padding: 1px;
    padding-right: 4px !important;
    border-width: 0px;
    border-style: solid;
    border-image: linear-gradient(to bottom, rgba(255,255,255,0) 0%, rgba(0,0,0,0) 30%, rgba(255,255,255,0.8) 40%, rgba(0,0,0,0) 75%) 1 100%;;
}

body.big-avatars .avatar img{
    padding: 1px;
    border: 1px solid var(--SmartThemeEmColor);
    margin: 4px 0px 0px 0px;
}

.character_select .avatar,
.group_member .avatar,
#group_avatar_preview .avatar {
    --big-avatar-height-factor: 1.8 !important;
    --big-avatar-width-factor: 1.2 !important;
    --big-avatar-border-factor: 5 !important;
}

body.big-avatars.charListGrid .character_select .avatar:not(.add_avatar), body.big-avatars.charListGrid .avatar_collage {
    align-items: unset;
    margin: 0px;
    overflow: clip;
    background: linear-gradient(315deg, rgba(255,255,255,0.20) 0%, rgba(0,0,0,0) 60%, rgba(255,255,255,0.8) 70%, rgba(0,0,0,0) 90%);
    border: 2px solid;
    border-image-source: linear-gradient(315deg, rgba(255,255,255,0.20) 0%, rgba(0,0,0,0) 60%, rgba(255,255,255,0.9) 70%, rgba(0,0,0,0) 90%);
    border-image-slice: 1;
    box-shadow: 3px 1px 5px var(--SmartThemeEmColor);
    --big-avatar-height-factor: 3 !important;
    --big-avatar-width-factor: 2 !important;
    --big-avatar-border-factor: 2 !important;
    border-radius: unset;
    padding: 2px 0px 0px 2px;
    border-radius: 4px 0px 4px;
}

body.big-avatars .avatars_inline {
    max-height: calc(var(--avatar-base-height) + 2 * var(--avatar-base-border-radius));
}

.character_select.is_fav .avatar, .group_select.is_fav .avatar, .group_member.is_fav .avatar, .avatar.is_fav {
    background: linear-gradient(315deg, rgba(255,255,255,0.20) 0%, rgba(0,0,0,0) 60%, var(--SmartThemeUnderlineColor) 70%, rgba(0,0,0,0) 90%) !important;;
    border-image-source: linear-gradient(315deg, var(--SmartThemeUnderlineColor) 0%, rgba(0,0,0,0) 60%, var(--SmartThemeUnderlineColor) 70%, rgba(0,0,0,0) 90%) !important;;
    border-image-slice: 1;
    outline: none !important;
    box-shadow: 1px 1px 5px var(--SmartThemeUnderlineColor) !important;
}

.character_select.is_fav .ch_name, .group_select.is_fav .ch_name, .group_member.is_fav .ch_name {
    color: var(--SmartThemeUnderlineColor);
}

.bogus_folder_select, .character_select, .group_select{
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 2px;
    border-radius: 0px;
    cursor: pointer;
    margin: 0px;
}

#charListFixedTop {
  display: none;
  transform-origin: left top;
}

#right-nav-panel:has(#rm_PinAndTabs:hover, #rm_PinAndTabs:focus-within) #charListFixedTop,
#charListFixedTop:hover,
#charListFixedTop:focus-within {
  display: block;
}

#rm_button_selected_ch h2::before {
  vertical-align: middle;
  text-align: center;
  content: "\f13a";
  margin-right: 6px;
  line-height: 10%;
  font-family: "Font Awesome 6 Free";
}

#right-nav-panel:has(#rm_PinAndTabs:hover, #rm_PinAndTabs:focus-within, #charListFixedTop:hover, #charListFixedTop:focus-within) #rm_button_selected_ch h2::before {
  content: "\f139";
}

#send_form:hover #qr--bar {
height: 40px; 
transition: 0.1s;
}

#qr--bar{
transition: 0.1s;
border-top: 6px solid var(--SmartThemeQuoteColor);
height: 4px;
}

.mes .mes_buttons {
  visibility: hidden;
  opacity: 0;
}
.mes:hover .mes_buttons {
  visibility: visible;
  opacity: 1;
}

#options {
display: block !important;
border-top: 10px groove var(--SmartThemeQuoteColor);
height: 0px;
width: 2.2em;
transition: 0s;
overflow-x: auto;

}

#options:hover {
height: auto !important;
width: auto !important;
}

.edit_textarea{
font-family: Consolas, monospace;
}
.world_entry_form_control textarea{
font-family: Consolas, monospace;
}

.mes_edit.fa-solid.fa-pencil {
  content: "\f303";
  font-size: x-large;
}

.mes_buttons, .extraMesButtons {
display: contents;
}

:root {
    --mainFontFamily: "DM Mono", monospace;
    /*
    --mainFontFamily: 'Anonymous Pro', monospace;
    --mainFontFamily: 'Courier Prime', monospace;
    --mainFontFamily: 'Cousine', monospace;
    --mainFontFamily: 'Cutive Mono', monospace;
    --mainFontFamily: 'DM Mono', monospace;
    --mainFontFamily: 'Fira Mono', monospace;
    --mainFontFamily: 'IBM Plex Mono', monospace;
    --mainFontFamily: 'Inconsolata', monospace;
    --mainFontFamily: 'Nanum Gothic Coding', monospace;
    --mainFontFamily: 'PT Mono', monospace;
    --mainFontFamily: 'Roboto Mono', monospace;
    --mainFontFamily: 'Share Tech Mono', monospace;
    --mainFontFamily: 'Source Code Pro', monospace;
    --mainFontFamily: 'Space Mono', monospace;
    --mainFontFamily: 'Ubuntu Mono', monospace;
    --mainFontFamily: 'VT323', monospace;
    */

    --border-radius: 0px;
    --border-width: 1px;
    --border-style: solid; /* dotted | dashed | solid | double | groove | ridge | inset | outset */
}

* {
	border-color: var(--SmartThemeEmColor) !important;
    border-radius: 0px !important;
}

hr {
	background-image: unset !important;
	border: 1px !important;
	border-style: solid !important;
	border-color: var(--SmartThemeEmColor) !important;
}


*:hover {
    border-color: var(--SmartThemeBorderColor) !important; /* Change this to your desired color */
}

#chat, #form_sheld {
	width: calc( 100dvw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--char-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--settings-panel-width) - var(--stcdxcodex) - 18px) !important;
	border: none !important;
	margin: unset !important;
}

#sheld {
	margin: 0px 0px 0px 6px ;
	height: 99.5dvh;
	left: calc(var(--nav-bar-width) + var(--char-panel-width) + var(--cfgConfig-panel-width) + 6px) !important;
	width: calc( 100dvw - var(--nav-bar-width) - var(--cfgConfig-panel-width) - var(--char-panel-width) - var(--expression-image-width) - var(--authors-note-panel-width) - var(--settings-panel-width) - var(--stcdxcodex) - 18px);
}

.drawer-content {
	position: fixed;
	width: calc(var(--drawer-content-panel-width) - 18px);
	left: calc(var(--nav-bar-width) + var(--char-panel-width) + 6px) ;
	margin: 6px !important;
	border-style: solid !important;
	border-width: 1px !important;
	border-radius: var(--border-radius) !important;

}

.ch_name .flex-container.flex1 {
	margin: 6px;
}

#right-nav-panel {
	margin: 6px 0px 0px 12px !important;
    left: calc(var(--nav-bar-width)) !important;
	height: 99dvh !important;
	width: calc(var(--char-panel-width) - 6px) !important;
}

#left-nav-panel {
	width: calc(var(--settings-panel-width) - 6px) !important;
	height: 99dvh !important;
}

body.big-avatars .avatar img {
	margin: 0px !important;
}

h3.margin0 span[data-i18n='User Settings'] {
    display: none;
}

#top-settings-holder {
	border-style: solid !important;
	border-width: 1px !important;
	border-radius: var(--border-radius) !important;
	margin: 6px 0px 0px 6px;
	position: absolute;
	height: 99dvh;
	width: calc(var(--nav-bar-width));

}

#floatingPrompt:not(.maximized) {
	height: 99dvh !important;
	width: calc(var(--authors-note-panel-width) - 6px);
}

.name_text {
	position: absolute;
	top: 0;
	margin: -10px 4px;
	background: black;
}

body.bubblechat .mes {
	border-style: solid !important;
	border-width: 1px !important;
	border-radius: var(--border-radius) !important;
	padding: 0px;
	margin: 6px 0px 6px 0px;
}

#user-settings-block::before {
    position: absolute;
	background: var(--SmartThemeBlurTintColor);
    margin: -18px 4px;
    padding: 0 3px;
    z-index: 9;
}

#cfgConfig {
	left: calc(var(--nav-bar-width) + var(--char-panel-width) + 6px);
	width: calc(var(--cfgConfig-panel-width) - 6px);
	height: 99dvh;

}

#logprobsViewer:not(.maximized) {
	right: calc(var(--settings-panel-width) + var(--authors-note-panel-width) + var(--stcdxcodex) + 6px);
	width: calc(var(--expression-image-width) - 6px);
}

#user-settings-block {
    border-style: solid !important;
	border-width: 1px !important;
	border-radius: var(--border-radius) !important;
	margin: 10px;
	padding: 8px;
	overflow: visible;
}

#user-settings-block::before {
    content: "User Settings";
}

.mes_block{
	padding: 0px 6px 0px !important;
}


body .stcdx--root {
	margin: 6px !important;
	width: calc(var(--stcdxcodex) - 6px);
}

body .stcdx--root > .stcdx--content, body .stcdx--root > .stcdx--editor {
	 height: unset !important;
}

#extensionTopBar {
    margin: 6px 0px 0px 0px;
}
